create table fa_attachment
(
    id          int unsigned auto_increment comment 'ID'
        primary key,
    category    varchar(50)  default ''      null comment '类别',
    admin_id    int unsigned default '0'     not null comment '管理员ID',
    user_id     int unsigned default '0'     not null comment '会员ID',
    url         varchar(255) default ''      null comment '物理路径',
    imagewidth  varchar(30)  default ''      null comment '宽度',
    imageheight varchar(30)  default ''      null comment '高度',
    imagetype   varchar(30)  default ''      null comment '图片类型',
    imageframes int unsigned default '0'     not null comment '图片帧数',
    filename    varchar(100) default ''      null comment '文件名称',
    filesize    int unsigned default '0'     not null comment '文件大小',
    mimetype    varchar(100) default ''      null comment 'mime类型',
    extparam    varchar(255) default ''      null comment '透传数据',
    createtime  int                          null comment '创建日期',
    updatetime  int                          null comment '更新时间',
    uploadtime  int                          null comment '上传时间',
    storage     varchar(100) default 'local' not null comment '存储位置',
    sha1        varchar(40)  default ''      null comment '文件 sha1编码'
)
    comment '附件表' collate = utf8mb4_unicode_ci;

