create table fa_auth_rule
(
    id          int unsigned auto_increment
        primary key,
    type        enum ('menu', 'file') default 'file'        not null comment 'menu为菜单,file为权限节点',
    pid         int unsigned          default '0'           not null comment '父ID',
    name        varchar(100)          default ''            null comment '规则名称',
    title       varchar(50)           default ''            null comment '规则名称',
    icon        varchar(50)           default ''            null comment '图标',
    url         varchar(255)          default ''            null comment '规则URL',
    `condition` varchar(255)          default ''            null comment '条件',
    remark      varchar(255)          default ''            null comment '备注',
    ismenu      tinyint unsigned      default '0'           not null comment '是否为菜单',
    menutype    enum ('addtabs', 'blank', 'dialog', 'ajax') null comment '菜单类型',
    extend      varchar(255)          default ''            null comment '扩展属性',
    py          varchar(30)           default ''            null comment '拼音首字母',
    pinyin      varchar(100)          default ''            null comment '拼音',
    createtime  int                                         null comment '创建时间',
    updatetime  int                                         null comment '更新时间',
    weigh       int                   default 0             not null comment '权重',
    status      varchar(30)           default ''            null comment '状态',
    constraint name
        unique (name)
)
    comment '节点表' collate = utf8mb4_unicode_ci;

create index pid
    on fa_auth_rule (pid);

create index weigh
    on fa_auth_rule (weigh);

