create table fa_config
(
    id      int unsigned auto_increment
        primary key,
    name    varchar(30)  default '' null comment '变量名',
    `group` varchar(30)  default '' null comment '分组',
    title   varchar(100) default '' null comment '变量标题',
    tip     varchar(100) default '' null comment '变量描述',
    type    varchar(30)  default '' null comment '类型:string,text,int,bool,array,datetime,date,file',
    value   text                    null comment '变量值',
    content text                    null comment '变量字典数据',
    rule    varchar(100) default '' null comment '验证规则',
    extend  varchar(255) default '' null comment '扩展属性',
    setting varchar(255) default '' null comment '配置',
    constraint name
        unique (name)
)
    comment '系统配置' collate = utf8mb4_unicode_ci;

